import axios from "axios";
import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { getProductsReq, getProductsSuccess } from "../Redux/actions";
import "./Products.css"
export const Products = () => {

  const {products, isLoading} = useSelector((state)=>({
      products : state.products,
      isLoading : state.isLoading
  }))

  const dispatch = useDispatch()

  useEffect(() => {
    //   dispatch an action to the store
    // dont make call here
    // handle it as thunk call in actions.js
    dispatch(getProductsReq())
    axios("https://movie-fake-server.herokuapp.com/products")
    .then((res)=>{
      console.log(res.data)

    dispatch(getProductsSuccess(res.data))
    })


  }, []);


  const handleSort = (e) => {
    // dispach handle sort action to the store
  };
  return (
    <>
      <h2>Products</h2>

      {isLoading&& <img src={"https://c.tenor.com/TGQu0qigTgQAAAAC/colorful-loading.gif"} style={{width: "170px"}} />}
      <br />
      <select onChange={handleSort}>
        <option>--sort by --</option>
        <option value="asc">low to high</option>
        <option value="desc">high to low</option>
      </select>


      <div className="products-list">
       {products[0]?.map((e)=>{
         return <Link className="Link" to={`/products/${e.id}`}><div className="card" key={e.id}>
         <img src={e.image} alt="" />
          <h4>Brand: {e.brand}</h4>
         <p>Price: {e.price} /- Rs</p>
     </div></Link>
       })}
      </div>
    </>

   );
};
